        $data = '016d5cf348fa3800ad20eb5a15c4b51c0300000073b877aa53ddb3090000000073b877aad92d300ba01feb5a73b877aa2d205b1e54125a5a';
        $got = $this->deserializeIntoComparableObject($data);
